def slurper = new XmlSlurper()
log.info messageExchange.responseContent
def response = slurper.parseText(messageExchange.responseContent)

foundSRM = 0
foundBTE = 0
foundSRMAHM = 0

response.system.each {

	systemId = it.'@systemId'
	systemType = it.'@systemType'
	systemVersion = it.'@systemVersion'

	assert systemId != null
	assert systemType != null
	assert systemVersion != null

	if ( systemId == "AEE200" ) {
		assert systemType == "SRM"
		foundSRM = 1
	}
	if ( systemId == "BTETEST" ) {
		assert systemType == "BTE"
		foundBTE = 1
	}
	if ( systemId == "ADS100" ) {
		assert systemType == "SRM"
		foundSRMAHM = 1
	}

}

assert foundSRM == 1
assert foundBTE == 1
assert foundSRMAHM == 1

