	userB64 = providedUser
	secret = providedSecret
}
else{
    def String errorMessage = ""
    final JTextField usernameField = new JTextField();
    final JPasswordField pinField = new JPasswordField();
    final JTextField tokenField = new JTextField();
    final JComponent[] components = [new JLabel("Username"), usernameField, new JLabel("PIN"), pinField, new JLabel("OTP"), tokenField];
    if (JOptionPane.showConfirmDialog(null, components, "Enter your RSA credentials", JOptionPane.OK_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE) == JOptionPane.OK_OPTION) {
        // Check Username
        if(!Pattern.matches("[a-zA-Z0-9]+", usernameField.getText())){
            errorMessage = "Invalid/Missing Username! "
        }
        // Check Pin
        if (!Pattern.matches("[0-9]{4}", new String(pinField.getPassword()))) {
     	   errorMessage = errorMessage + "\nPIN must consist of 4 digits! "
        }
        //Check Token
        if (!Pattern.matches("[0-9]{6}", tokenField.getText())) {
 	       errorMessage = errorMessage + "\nOTP must consist of 6 digits! "
        }
        //show Errormessage if input was incorrect
        if(!errorMessage.equals("")){
            JOptionPane.showMessageDialog(null, errorMessage+"\n\nTeststep Failed!", "ERROR" , JOptionPane.ERROR_MESSAGE);
            testRunner.fail(errorMessage)
        }
        //Everything was ok, set Variables
        else{
            userB64 = usernameField.getText().bytes.encodeBase64().toString()
            secret = new String(pinField.getPassword()) + tokenField.getText()
        }
    }
    //show Message if Cacel was clicked
    else {
        JOptionPane.showMessageDialog(null, "Teststep was canceled!", "Canceled" , JOptionPane.INFORMATION_MESSAGE);
        testRunner.cancel("Canceled!")
    }
